'use strict';

var parseCommits = require( './../lib' );

// Commits for a non-namespace package:
var commits = parseCommits( 'assert/contains' );
console.log( 'Commits for `assert/contains`:' );
console.log( commits );
// => [...]

// Commits for a namespace package:
commits = parseCommits( 'math/base/special' );
console.log( 'Commits for `math/base/special`:' );
console.log( commits );
// => [...]

// Commits for entire project:
commits = parseCommits( '' );
console.log( 'Commits for entire project:' );
console.log( commits );
// => [...]

// Commits for non-existent package:
commits = parseCommits( 'beep/boop' );
console.log( 'Commits for `beep/boop`:' );
console.log( commits );
// => []
