'use strict';

// MODULES //

var path = require( 'path' );
var tape = require( 'tape' );
var readFile = require( '@stdlib/fs/read-file' ).sync;
var bundle = require( './../lib' );


// FIXTURES //

var sampleInputPath = path.join( __dirname, 'fixtures/input/a.js.txt' );
var sampleOutput = readFile( path.join( __dirname, 'fixtures/output/bundle.js.txt' ), 'utf8' );


// TESTS //

tape( 'bundle a file', function test( t ) {
	var bundlerOpts = { };

	function onBundle( err, result ) {
		t.equal( err, null, 'bundler completes without errors' );
		t.equal( result, sampleOutput, 'bundler produces correct output' );

		t.end();
	}

	bundle( sampleInputPath, bundlerOpts, onBundle );
});
