'use strict';

var path = require( 'path' );
var ls = require( '@stdlib/_tools/pkgs/browser-compatible' );
var bundle = require( './../lib' );

var opts = {
	'ignore': [
		'**/_tools/**'
	],
	'dir': './@stdlib/math/base/special'
};

/*
var pkgs = [
	'@stdlib/math/base/special/fibonacci',
	'@stdlib/math/base/assert/is-integer',
	'@stdlib/math/base/assert/is-nan',
	'@stdlib/math/base/special/floor',
	'@stdlib/constants/math/float64-max-safe-nth-fibonacci',
	'@stdlib/plot/ctor',
	'@stdlib/stats/base/dmeankbn',
	'@stdlib/plot/components/svg/defs'
	'@stdlib/array/pool'
];
*/

ls( opts, onPkg );

function onPkg( errors, pkgs ) {
	if ( errors ) {
		throw errors;
	}

	var opts = { };

	bundle( pkgs, path.join(__dirname, 'dist'), opts, onBundled );
}

function onBundled( err ) {
	if ( err instanceof Error ) {
		throw err;
	}
}
