'use strict';

var path = require('path');
var bundle = require( './../lib' );
var ls = require('@stdlib/_tools/pkgs/names');

ls({
	'pattern': '@stdlib/plot/components/svg/marks/**/package.json'
}, function (errors, pkgs) {
	if (errors) {
		throw errors;
		process.exit(1);
	}

	pkgs = pkgs.filter( pkg => ! /^@stdlib\/(streams|_tools|fs|bench|namespace|repl|tools)\b/.test(pkg))

	pkgs = [
		'@stdlib/math/base/special/fibonacci',
		'@stdlib/math/base/assert/is-integer',
		'@stdlib/math/base/assert/is-nan',
		'@stdlib/math/base/special/floor',
		'@stdlib/constants/math/float64-max-safe-nth-fibonacci'
	];

	var opts = { };

	bundle( pkgs, path.join(__dirname, 'dist'), opts, function (err, result) {
		if ( err ) {
			throw err;
			process.exit(1);
		}
	});
});
