'use strict';

var path = require( 'path' );
var ls = require( '@stdlib/_tools/pkgs/browser-compatible' );
var bundle = require( './../lib' );

var opts = {
	'ignore': [
		'@stdlib/_tools/**',
		'@stdlib/tools/**',
		'@stdlib/repl/**',
		'@stdlib/namespace/**/package.json'
	]
};

ls( opts, onPkgs );

// Alternatively, to test single packages: onPkgs( null, [ '@stdlib/plot/ctor' ] );

function onPkgs( errors, pkgs ) {
	if ( errors ) {
		throw errors;
	}

	var opts = { };

	bundle( pkgs, path.join(__dirname, 'dist'), opts, onBundled );
}

function onBundled( err ) {
	if ( err instanceof Error ) {
		throw err;
	}
}
