'use strict';

// MODULES //

var fs = require( 'fs' );


// MAIN //

/**
 * Create a page to exhaustively test ESM resolution.
 *
 * @param {Array} pkgs - list of packages to be bundled
 * @param {string} outputFile - HTML output destination
 * @param {Callback} clbk - callback executed on completion
 * @returns {void}
 */
function createDebugPage( pkgs, outputFile, clbk ) {
	var lines;
	var i;

	lines = [
		'<!doctype html>',
		'<html>',
		'<head>',
		'</head>',
		'<body>',
		'<script type="module">'
	];

	for (i = 0; i < pkgs.length; i++) {
		lines.push( 'import foo' + i + ' from \'./' + pkgs[ i ].pkg + '.js\'' );
	}

	lines = lines.concat([
		'</script>',
		'</body>',
		'</html>'
	]);

	fs.writeFile( outputFile, lines.join('\n'), clbk );
}


// EXPORTS //

module.exports = createDebugPage;
