'use strict';

// MODULES //

var logger = require( 'debug' );
var objectKeys = require( '@stdlib/utils/keys' );


// VARIABLES //

var debug = logger( 'esm-create-dist:vendor_modules' );


// MAIN //

/**
 * Collect a de-duped list of all encountered vendor modules.
 *
 * @param {Array} deps - list of deps from @stdlib/_tools/pkgs/browser-deps module
 * @returns {Array} list of vendor deps
 */
function computeVendorModules( deps ) {
	var pkgDeps;
	var obj;
	var dep;
	var i;
	var j;

	obj = {};
	for ( i = 0; i < deps.length; i++ ) {
		debug( 'Computing vendor deps for package: %s (%d of %d)', deps[ i ].pkg, i, deps.length );

		pkgDeps = deps[ i ].deps;

		for ( j = 0; j < pkgDeps.length; j++ ) {
			dep = pkgDeps[ j ];

			if ( !/^@stdlib/.test( dep ) ) {
				obj[ dep ] = true;
			}
		}
	}

	return objectKeys(obj);
}


// EXPORTS //

module.exports = computeVendorModules;
