'use strict';

var path = require( 'path' );
var createVendorModules = require( './../lib' );

var moduleList = [
	'buffer',
	'd3-scale',
	'd3-format',
	'os',
	'events',
	'vdom-to-html',
	'virtual-dom/h',
	'debug',
	'd3-time-format',
	'd3-shape',
	'readable-stream',
	'fs',
	'path',
	'http',
	'child_process'
];

var outputDirectory = path.join( __dirname, 'dist' );

createVendorModules( moduleList, outputDirectory, onBundle );

function onBundle( err ) {
	if ( err instanceof Error ) {
		console.error( err );
	}
}
