'use strict';

// MODULES //

var fs = require('fs');
var writeFile = require('@stdlib/fs/write-file').sync;
var path = require( 'path' );
var logger = require( 'debug' );
var extractPackageFiles = require('./extract_package_files.js');


// VARIABLES //

var debug = logger( 'esm:extract-files' );


// MAIN //

function build( roots, destinationBasePath, options, clbk ) {
	var err;
	var metadata = [];

	if( Array.isArray(roots) ) {
		for (var i = 0; i < roots.length; i++) {
			metadata.push(extractPackageFiles( roots[i], destinationBasePath, options ));
		}
	} else {
		metadata.push(extractPackageFiles( roots, destinationBasePath, options ));
	}

	err = writeFile( path.join( destinationBasePath, 'manifest.json' ), JSON.stringify(metadata, null, 2) );

	if ( err instanceof Error ) {
		throw err;
	}

	return metadata;
}


// EXPORTS //

module.exports = build;
