'use strict';

// MODULES //

var tape = require( 'tape' );
var transform = require( './../lib' );


// TESTS //

tape( 'vendor require', function test( t ) {
	var input;
	var output;

	input = "var foo = require( 'foo-bar' );";
	output = transform( input );

	t.equal(output, "var foo = require( '@stdlib/vendor/foo-bar.js' );");

	t.end();
});

tape( 'path require', function test( t ) {
	var input;
	var output;

	input = "var foo = require( 'foo/bar' );";
	output = transform( input );

	t.equal(output, "var foo = require( 'foo/bar' );");

	t.end();
});

tape( 'qualified require', function test( t ) {
	var input;
	var output;

	input = "var foo = require( 'foo-bar/index.js' );";
	output = transform( input );

	t.equal(output, "var foo = require( 'foo-bar/index.js' );");

	t.end();
});

tape( 'relative require', function test( t ) {
	var input;
	var output;

	input = "var foo = require( './foo-bar' );";
	output = transform( input );

	t.equal(output, "var foo = require( './foo-bar' );");

	t.end();
});
