'use strict';

// MODULES //

var path = require( 'path' );

var isBuffer = require( '@stdlib/assert/is-buffer' );
var isString = require( '@stdlib/assert/is-string' ).isPrimitive;
var isRegExp = require( '@stdlib/assert/is-regexp' );
var string2buffer = require( '@stdlib/buffer/from-string' );
var reFromString = require( '@stdlib/utils/regexp-from-string' );
var replace = require( '@stdlib/string/replace' );


// VARIABLES //

var IMPORT_REGEX = /\bimport\s+([\w\$]+)\s+from\s*("([\w-@\$\/\.js]+)"|'([\w-@\$\/\.js]+)')/g;


// MAIN //

function transformImports( sourcePath, baseDir, src, opts ) {
	var pathValidationRegExp;
	var sourceDir;
	var baseDir;
	var isStr;

	if (opts) {
		pathValidationRegExp = opts[ 'validatePath' ];
	}

	if ( pathValidationRegExp !== undefined && ! isRegExp( pathValidationRegExp ) ) {
		throw new Error( '`validatePath` must be a RegExp' );
	}

	sourceDir = path.resolve( path.dirname( sourcePath ) );

	isStr = isString( src );
	if ( !isStr && !isBuffer( src ) ) {
		throw new TypeError( 'invalid argument. First argument must be either a string or Buffer. Value: `' + src + '`.' );
	} else {
		src = src.toString();
	}

	function transformImport( str, identifier, _1, doubleQuotesImportPath, singleQuotesImportPath) {
		var relativePath;
		var importPath;
		var importBase;
		var importDir;
		var importExt;

		importPath = doubleQuotesImportPath === undefined ? singleQuotesImportPath : doubleQuotesImportPath;

		if ( pathValidationRegExp && ! pathValidationRegExp.test( importPath ) ) {
			throw new Error( 'Expected path to match `validatePath` but got path \'' + importPath + '\'' );
		}

		importPath = path.resolve( path.join( baseDir, importPath ) );

		// Decompose and reassemble the path so that we can be sure not to mix up directories with files with implicit extensions
		var importExt = path.extname( importPath );
		var importBase = path.basename( importPath, importExt );
		var importDir = path.dirname( importPath );

		if ( importExt === '' ) {
			importExt = '.js';
		}

		importPath = path.join( importDir, importBase + importExt );

		relativePath = path.relative(sourceDir, importPath);

		return 'import ' + identifier + " from './" + relativePath + "'";
	}

	src = replace( src, IMPORT_REGEX, transformImport );

	// If provided a source code buffer, return a source code buffer...
	if ( !isStr ) {
		return string2buffer( src );
	}
	return src;
}


// EXPORTS //

module.exports = transformImports;
