'use strict';

// MAIN //

/**
* Asynchronously removes a license header from each file in a provided file list.
*
* @param {(EmptyArray|StringArray)} files - file list
* @param {(string|Object|RegExp)} header - license header to remove
* @param {Callback} clbk - callback to invoke upon completion
* @returns {void}
*/
function transformCommonJS( files, header, clbk ) {
	clbk( new Error( 'async variant of module is unimplemented' ) );
}


// EXPORTS //

module.exports = transformCommonJS;
