'use strict';

// MODULES //

var isBuffer = require( '@stdlib/assert/is-buffer' );
var isString = require( '@stdlib/assert/is-string' ).isPrimitive;
var string2buffer = require( '@stdlib/buffer/from-string' );
var replace = require( '@stdlib/string/replace' );


// VARIABLES //

var SET_EXPORT_REGEX = /^setReadOnly\s*\(\s*(\w+)\s*,\s*['"](\w+)['"]\s*,\s*(\w+)\s*\)\s*;\s*$/mg;


// FUNCTIONS //

// MAIN //

/**
 * Transform CommonJS requires to ESM imports.
 *
 * @param {String} src - source to be transformed
 * @throws {TypeError} src input must be string or buffer
 * @returns {String} transformed source
 */
function insertNamedExports( src ) {
	var exports;
	var isStr;

	exports = [];

	/**
	 * Transform an export to an ESM named export.
	 *
	 * @private
	 * @param {String} str - matched string
	 * @param {String} namespace - package namespace
	 * @param {String} exportName - exported name
	 * @param {String} identifier - exported identifier
	 * @returns {String} transformed export statement
	 */
	function transformExport( str, namespace, exportName, identifier ) {
		exports.push( identifier + ' as ' + exportName );
		return str;
	}

	isStr = isString( src );
	if ( !isStr && !isBuffer( src ) ) {
		throw new TypeError( 'invalid argument. First argument must be either a string or Buffer. Value: `' + src + '`.' );
	} else {
		src = src.toString();
	}

	src = replace( src, SET_EXPORT_REGEX, transformExport );

	if ( exports.length > 0 ) {
		src += '\nexport { ' + exports.join(', ') + '};';
	}

	// If provided a source code buffer, return a source code buffer.
	if ( !isStr ) {
		return string2buffer( src );
	}
	return src;
}


// EXPORTS //

module.exports = insertNamedExports;
