'use strict';

// MODULES //

var path = require( 'path' );
var logger = require( 'debug' );
var readJSON = require( '@stdlib/fs/read-json' ).sync;
var isString = require( '@stdlib/assert/is-string' ).isPrimitive;
var isPlainObject = require( '@stdlib/assert/is-plain-object' );


// VARIABLES //

var debug = logger( 'browser-pkg-deps:sync:resolve-aliases' );
var FOPTS = {
	'encoding': 'utf8'
};


// MAIN //

/**
* Synchronously resolves package dependency aliases.
*
* @private
* @param {(ObjectArray)} pkgs - packages
* @returns {(ObjectArray|Error)} results or an error
*/
function resolve( pkgs ) {
	var data;
	var len;
	var pkg;
	var o;
	var i;
	var k;

	len = pkgs.length;

	debug( 'Resolving %d package aliases...', len );
	for ( i = 0; i < len; i++ ) {
		pkg = pkgs[ i ];
		k = i + 1;

		debug( 'Resolving package: %s (%d of %d)...', pkg.pkg, k, len );
		data = readJSON( path.join( pkg.dir, 'package.json' ), FOPTS );
		if ( data instanceof Error ) {
			debug( 'Encountered an error when resolving package meta data: %s (%d of %d). Error: %s', pkg.pkg, k, len, data.message );
			return data;
		}
		debug( 'Successfully resolved package meta data: %s (%d of %d).', pkg.pkg, k, len );

		if ( isString( data.browser ) ) {
			o = {};

			// Check for a valid `main` field...
			if ( isString( data.main ) ) {
				o[ data.main ] = data.browser;
			} else {
				o[ 'index.js' ] = data.browser;
			}
		} else if ( isPlainObject( data.browser ) ) {
			o = data.browser;
		} else {
			o = {};
		}
		debug( 'Successfully resolved package aliases: %s (%d of %d).', pkg.pkg, k, len );
		pkg.aliases = o;
	}
	debug( 'Resolved all package aliases.' );
	return pkgs;
}


// EXPORTS //

module.exports = resolve;
